class Fader{constructor(){this.fadeElements=document.querySelectorAll(".fade"),this.options={rootMargin:"0px 0px 100px 0px"},this.fadeObserver=new IntersectionObserver((entries,observer)=>{entries.forEach(entry=>{let element=entry.target;entry.isIntersecting?(element.hasAttribute("data-fade-speed")?element.setAttribute("style",`opacity:1;transition:opacity ${element.getAttribute("data-fade-speed")} ease-in`):element.setAttribute("style","opacity:1;transition:opacity .7s ease-in"),observer.unobserve(entry.target)):element.hasAttribute("data-fade-speed")?element.setAttribute("style",`opacity:0;transition:opacity ${element.getAttribute("data-fade-speed")} ease-in`):element.setAttribute("style","opacity:0;transition:opacity .7s ease-in")})},this.options)}init(){this.fadeElements.forEach(fadeElement=>{this.fadeObserver.observe(fadeElement)})}}