window.addEventListener("DOMContentLoaded",()=>{const navbar=document.querySelector("nav"),section1=document.querySelector("section:nth-child(1)"),options={rootMargin:"70% 0px 0px 0px"},navObserver=new IntersectionObserver(entries=>{entries.forEach(entry=>{entry.isIntersecting?(navbar.classList.remove("nav-scrolled","shadow"),document.getElementById("goup").classList.remove("float-in")):(navbar.classList.add("nav-scrolled","shadow"),document.getElementById("goup").classList.add("float-in"))})},options);navObserver.observe(section1)});